import React from 'react';

import './App.css';
import { motion } from 'framer-motion';

function App() {
  const spring = {
    type: "mirror",
    damping: 10,
    stiffness: 100,
    repeat: Infinity,

  }
  return (
    <>
      <motion.div
        className='animBlock'
        animate={{ scale: 2, rotate: 360, borderRadius: '50%', backgroundColor: '#f00' }}
        // whileHover={{ scale: 1.5, rotate: 0, borderRadius: '0%', backgroundColor: '#fff' }}
        transition={spring}
      />
    </>
  );
}

export default App;
