import React from 'react';

import './App.css';
import { motion } from 'framer-motion';

function App() {

  const variants = {
    active: {
      backgroundColor: "#f00"
    },
    inactive: {
      backgroundColor: "#fff",
      transition: { duration: 20 }
    }
  }



  return (
    <div id='animContainer'>
      <motion.div
        className='animBlock'
        animate={{ scale: 2, rotate: 360, borderRadius: '50%', backgroundColor: '#f00' }}
        // whileHover={{ scale: 1.5, rotate: 0, borderRadius: '0%', backgroundColor: '#fff' }}
        transition={{ duration: 2, repeat: Infinity, repeatType: 'mirror', ease: 'anticipate' }}
      />
      <motion.div variants={variants} className='animBlock' animate="active" />
    </div>
  );
}

export default App;
