import React from 'react';

import './App.css';
import { motion } from 'framer-motion';

function App() {
  return (
    <>
      <motion.div
        className='animBlock'
        animate={{ scale: 2, rotate: 360, borderRadius: '50%', backgroundColor: '#f00' }}
        whileHover={{ scale: 1.5, rotate: 360, borderRadius: '50%', backgroundColor: '#fff' }}
        transition={{ duration: 2, repeat: Infinity, repeatType: 'mirror', ease: 'anticipate' }}
      />
    </>
  );
}

export default App;
